INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (1, 'impede', 'verb', 'to slow or block progress, movement, or development.', 'Heavy traffic impeded our progress to the airport.', '["obstruction","delay","resistance","inhibition"]'::jsonb, '["assist","facilitate","enable","promote","advance"]'::jsonb, '{}'::jsonb, 'entraver — often used for physical or bureaucratic obstruction.', 'препятствовать (prepyatstvovat’) — to hinder or stand in the way.', 'C1', NULL, true, '"2025-10-21T02:04:31.629Z"'::jsonb, NULL, NULL, 'A word born of the body’s weight now moves through systems. Its journey from fettered feet to clogged bureaucracy traces the civilization’s shift from moral to mechanical order.', '["impede progress — to slow development or advancement (bureaucratic, academic)","impede access — to make entry or use difficult (rights, equity, logistics)","impede justice — to obstruct or delay legal process (legal idiom)"]'::jsonb, '{}'::jsonb, '{"primary":"To obstruct or delay the movement or progress of something.","tertiary":"Figurative: to place barriers in the path of advancement or success.","secondary":"To hinder or block the functioning or development of a process or person."}'::jsonb, '["impediment","impeded","impeding"]'::jsonb, '["obstruction","delay","resistance","inhibition"]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["entraver","bloquer","ralentir"]'::jsonb, '["pied — foot"]'::jsonb, '["мешать","затруднять","препятствовать"]'::jsonb, '["препятствие — obstacle"]'::jsonb, '["impede progress — bureaucratic and technical usage.","impede justice — legal idiom retaining a moral echo.","impede access — rights and equity discourse."]'::jsonb, 'Impede walks with a quiet weight. He’s the slow hand on a turning wheel, the tangle in a runner’s feet. He isn’t cruel; he just holds things where they are. You meet him in paperwork, in hesitation, in the long breath before action. He doesn’t destroy. He delays.', 'mastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (527, 'plausible', 'adjective', 'seeming reasonable or probable; appearing worthy of belief or acceptance.', 'Her explanation sounded plausible, but we needed more evidence.', '["credible","believable","reasonable","convincing","persuasive","specious"]'::jsonb, '["implausible","incredible","unbelievable","absurd","ridiculous"]'::jsonb, '{}'::jsonb, 'plausible — same meaning; ''vraisemblable'' as alternative.', 'правдоподобный (pravdopodobnyy) — seeming true or believable.', 'B2', NULL, false, '"2025-10-25T22:31:27.715Z"'::jsonb, NULL, NULL, 'Plausible moved from theatrical applause to philosophical probability to social respectability to persuasive strategy. Her journey tracks the shift from ''worthy of approval'' to ''appearing reasonable''—from judgment to appearance.', '["plausible explanation — believable account or reason (general use)","plausible deniability — ability to deny involvement convincingly (legal, political)","sound plausible — appear reasonable upon hearing (conversational)"]'::jsonb, '{}'::jsonb, '{"primary":"Appearing reasonable or credible; seeming worthy of belief.","tertiary":"Capable of being applauded or approved as reasonable.","secondary":"Having the appearance of truth without necessarily being true."}'::jsonb, '["plausibility","plausibly"]'::jsonb, '["believability","credibility","reasonableness","appearance","persuasion"]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["vraisemblable","crédible","raisonnable"]'::jsonb, '["applaudir","applaudissement"]'::jsonb, '["правдоподобный","убедительный","разумный"]'::jsonb, '["аплодировать — to applaud"]'::jsonb, '["plausible explanation — believable account that seems reasonable.","plausible deniability — ability to deny involvement convincingly.","sound plausible — appear reasonable when heard or considered."]'::jsonb, 'Plausible sits at the threshold of belief. She presents herself as reasonable, dressed in the clothes of acceptability. She doesn''t promise truth—only the appearance of it. You meet her in convincing arguments that might be wrong, in stories that could be true, in explanations that feel right even when they''re incomplete. Her power is persuasion through seeming, not certainty.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
