INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (542, 'ubiquitous', 'adjective', 'present, appearing, or found everywhere; extremely common or widespread.', 'Smartphones have become ubiquitous in modern life.', '["omnipresent","pervasive","everywhere","universal","commonplace"]'::jsonb, '["rare","scarce","uncommon","unusual","absent"]'::jsonb, '{}'::jsonb, 'ubiquitaire — same meaning; ''omniprésent'' as more common alternative.', 'вездесущий (vezdesushchiy) — present everywhere, omnipresent.', 'C1', NULL, false, '"2025-10-25T22:31:27.745Z"'::jsonb, NULL, NULL, 'Ubiquitous traces the journey from divine attribute to physical property to commercial strategy to digital reality. Starting as the impossible word for divine presence, it became the ordinary word for what literally fills all spaces—from theology to actuality.', '["ubiquitous presence — appearing everywhere (formal, descriptive)","ubiquitous technology — widespread and constantly present tech (contemporary discourse)","seemingly ubiquitous — appearing to be everywhere (emphatic)"]'::jsonb, '{}'::jsonb, '{"primary":"Present or appearing everywhere at the same time.","tertiary":"Seeming to be everywhere simultaneously.","secondary":"Excessively or extremely common or widespread."}'::jsonb, '["ubiquity","ubiquitously"]'::jsonb, '["presence","pervasiveness","omnipresence","universality","commonness"]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["omniprésent","répandu","généralisé"]'::jsonb, '["partout — everywhere"]'::jsonb, '["вездесущий","повсеместный","всепроникающий"]'::jsonb, '["везде — everywhere","существовать — to exist"]'::jsonb, '["ubiquitous presence — appearing everywhere simultaneously.","ubiquitous technology — widespread and constantly accessible tech infrastructure.","seemingly ubiquitous — appearing to exist in all places."]'::jsonb, 'Ubiquitous has learned to be everywhere without ever leaving himself behind. He multiplies without reducing, spreads without thinning. You find him in the repeated logo, in the phrase everyone knows, in the thing that cannot be escaped. He is the opposite of solitude—presence made infinite, identity distributed across all spaces until space itself becomes his name.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
