INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (187, 'verisimilitude', 'noun', 'the appearance or quality of being true or real; the quality of seeming to be true.', 'The novel''s verisimilitude made readers believe the events had actually occurred.', '["plausibility","believability","realism","authenticity","credibility"]'::jsonb, '["implausibility","incredibility","unreality","falseness"]'::jsonb, '{}'::jsonb, 'vraisemblance — the appearance of truth; used in literature and philosophy.', 'правдоподобие (pravdopodobie) — truthlikeness, plausibility.', 'C2', NULL, false, '"2025-10-21T18:12:26.362Z"'::jsonb, NULL, NULL, 'Verisimilitude traces the journey from philosophical category to aesthetic principle to technological standard. Born in rhetoric''s gray zone between truth and persuasion, she became the measure by which we judge what feels real—proof that likeness can be as powerful as truth itself.', '["verisimilitude in fiction — believability in storytelling (literary criticism)","lack verisimilitude — fail to seem realistic or plausible (critique)","lend verisimilitude — add realistic detail to enhance believability (craft)"]'::jsonb, '{}'::jsonb, '{"primary":"The appearance or semblance of truth; the quality of appearing to be true or real.","tertiary":"In philosophy and law: the quality of being probable or likely based on available evidence.","secondary":"In literature and art: lifelike quality that makes fiction believable."}'::jsonb, '["verisimilar"]'::jsonb, '["truth","appearance","believability","realism","probability"]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["vraisemblance","plausibilité","crédibilité"]'::jsonb, '["vrai — true","semblable — similar"]'::jsonb, '["правдоподобие","достоверность","вероятность"]'::jsonb, '["правда — truth","подобный — similar"]'::jsonb, '["verisimilitude in fiction — the art of believable storytelling.","lack verisimilitude — the failure to convince or feel real.","lend verisimilitude — to add detail that creates belief."]'::jsonb, 'Verisimilitude wears the mask of truth without claiming to be it. She moves between the real and the crafted, making lies feel lived and fictions feel found. She isn''t honesty—she''s its shadow, its twin. You meet her in novels that breathe, in courtroom arguments that persuade, in designs that feel inevitable. She doesn''t promise truth. She promises its likeness.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cogn