INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (56, 'salient', 'adjective', 'most noticeable or important; prominent or striking.', 'The most salient feature of the proposal was its emphasis on sustainability.', '["prominent","notable","striking","conspicuous","remarkable","outstanding"]'::jsonb, '["obscure","inconspicuous","hidden","minor"]'::jsonb, '{}'::jsonb, 'saillant — literally ''leaping out''; used for prominence or protrusion.', 'выдающийся (vydaiushchiysya) — prominent, remarkable.', 'C1', NULL, true, '"2025-10-21T02:04:31.814Z"'::jsonb, NULL, NULL, '‘Salient’ traces the conversion of motion into meaning. Born in the leap of bodies and fountains, it became the figure for prominence in thought—a standing reminder that every idea once sprang from life’s movement.', '["salient feature — most prominent aspect (analysis, design)","salient point — main or central argument (rhetoric)","salient angle — projecting angle in military or architectural use (historical)","most salient — intensifier for central importance (journalism, commentary)"]'::jsonb, '{}'::jsonb, '{"primary":"Most noticeable or important; standing out prominently.","tertiary":"Figurative: striking or leaping to attention through force or clarity.","secondary":"Projecting outward, as in a salient angle or bulge."}'::jsonb, '["salience","saliently"]'::jsonb, '["prominence","emphasis","projection","distinctness","visibility"]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["saillant","remarquable"]'::jsonb, '["sauter — to leap","ressortir — to stand out"]'::jsonb, '["выдающийся","бросающийся в глаза"]'::jsonb, '["сальто — borrowed from Italian salto (leap)"]'::jsonb, '["salient point — the idea that leaps to mind.","salient feature — what stands out in view or argument.","salient angle — projection toward encounter or risk.","most salient — the peak of prominence."]'::jsonb, 'Salient is the leap made visible—the instant something crosses from hidden to seen. He’s the flash in the mind, the outcrop on the plain. He doesn’t whisper for attention; he springs toward it. Wherever meaning wants to be found, Salient has already taken a step forward.', 'learned') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
