INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (13, 'inherent', 'adjective', 'existing as a permanent, essential, or characteristic attribute of something.', 'Risk is inherent in all forms of investment.', '["intrinsic","innate","essential","built-in","fundamental","native"]'::jsonb, '["external","acquired","extrinsic","adventitious"]'::jsonb, '{}'::jsonb, 'inhérent — same meaning; formal and philosophical register.', 'присущий (prisyushchiy) — innate, belonging naturally to.', 'C1', NULL, false, '"2025-10-21T02:04:31.735Z"'::jsonb, NULL, NULL, '‘Inherent’ shows how a single image—something clinging within—became the foundation for ideas of essence, morality, and structure. It’s the story of adhesion turned into identity.', '["inherent risk — unavoidable or native to a system (finance, engineering)","inherent value — fundamental or intrinsic worth (philosophy, economics)","inherent right — natural or inborn entitlement (law, ethics)"]'::jsonb, '{}'::jsonb, '{"primary":"Belonging naturally; existing as an essential part of something.","tertiary":"Philosophical: existing within a substance rather than derived from something external.","secondary":"Permanently associated with and inseparable from the subject."}'::jsonb, '["inherence","inherently"]'::jsonb, '["essence","nature","quality","property","identity"]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["intrinsèque","propre à"]'::jsonb, '["adhérer","cohérent"]'::jsonb, '["врождённый","свойственный","присущий"]'::jsonb, '[]'::jsonb, '["inherent risk — unavoidable or native to the system.","inherent value — philosophical or economic sense of intrinsic worth.","inherent right — moral and political discourse on innate entitlement."]'::jsonb, 'Inherent is the quiet bond between a thing and what it cannot lose. He’s not loud about it—he simply abides. You find him in the weight of stone, in the pulse of justice, in the curve of instinct. He doesn’t add; he dwells. His power is the stillness of what already belongs.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (70, 'perfunctory', 'adjective', 'done quickly and without real interest, care, or effort.', 'He gave a perfunctory nod before leaving the room.', '["cursory","mechanical","superficial","indifferent","unthinking"]'::jsonb, '["thorough","careful","deliberate","attentive","conscientious"]'::jsonb, '{}'::jsonb, 'superficiel — lacking depth; ''mécanique'' for mechanical sense.', 'поверхностный (poverkhnostnyy) — superficial, lacking depth or sincerity.', 'C2', NULL, false, '"2025-10-21T02:04:31.835Z"'::jsonb, NULL, NULL, '‘Perfunctory’ charts the collapse of duty into disaffection. Born in Rome’s ethic of steadiness, he was remade by religion into guilt, and by industry into automation—a mirror of work emptied of its will.', '["perfunctory nod — minimal acknowledgment (social cue)","perfunctory smile — forced or insincere expression (emotional detachment)","perfunctory task — routine action done without care (workplace idiom)"]'::jsonb, '{}'::jsonb, '{"primary":"Done quickly and without real care or interest; mechanical.","tertiary":"Indifferent or apathetic in manner or attitude.","secondary":"Performed merely as a duty or routine; superficial."}'::jsonb, '["perfunctorily","perfunctoriness"]'::jsonb, '["carelessness","mechanical action","habit","routine","insincerity"]'::jsonb, '[]'::jsonb, '[]'::jsonb, '["superficiel","mécanique"]'::jsonb, '["fonction","exécution"]'::jsonb, '["поверхностный","формальный","механический"]'::jsonb, '[]'::jsonb, '["perfunctory nod — motion without meaning.","perfunctory smile — performance without warmth.","perfunctory ritual — habit without heart."]'::jsonb, 'Perfunctory walks among us like someone still doing their job long after the reason has gone. He smiles when he’s supposed to, nods when he must, checks the box, moves on. He’s not cruel—just tired. You’ve met him in a rushed ''thanks,'' a dry ''sorry,'' a promise said without weight. That’s who he is now. But he wasn’t always this way.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
