INSERT INTO quiz_materials (id, word_id, level, question_type, prompt, options, correct_answer, variant_data, reward_amount, created_at, updated_at) VALUES (86, 70, 6, 'story', 'Rebuild the full story of ''perfunctory''—beware the four false centuries. Conquer the beast for double silk.', '{"turns":["He worked among Roman officials who prized completion over feeling.","He became a prayer said by habit—words without heart.","He entered English bearing guilt for hollow acts.","Factories and offices gave him endless routines; he moved without soul.","He now lives in screens and schedules, still moving, still performing.","He was adopted by monks to describe joyful labor—the sacred duty that filled the soul with purpose.","He symbolized the rational perfection of craft—where every gesture became an expression of enlightened reason.","He inspired motivational slogans for workers—turning routine into revolutionary purpose."],"settings":["Rome","Medieval Church","Renaissance Humanism","Industrial Age","Digital Life","Carolingian Europe","Enlightenment England","American Pragmatism"],"red_herrings":["He was carved into temple inscriptions to bless crops.","He vanished after Rome''s fall, lost to the barbarians.","He re-emerged during the French Revolution as a virtue.","He was sung in factories to ward off fatigue."],"time_periods":["1st c. CE — Rome","12th c. — Medieval Church","16th c. — Renaissance Humanism","19th c. — Industrial Age","21st c. — Digital Life","8th c.","18th c.","20th c."]}'::jsonb, '{"1st c. CE — Rome → He worked among Roman officials who prized completion over feeling.","12th c. — Medieval Church → He became a prayer said by habit—words without heart.","16th c. — Renaissance Humanism → He entered English bearing guilt for hollow acts.","19th c. — Industrial Age → Factories and offices gave him endless routines; he moved without soul.","21st c. — Digital Life → He now lives in screens and schedules, still moving, still performing."}', '{"feedback":{"fail