INSERT INTO quiz_materials (id, word_id, level, question_type, prompt, options, correct_answer, variant_data, reward_amount, created_at, updated_at) VALUES (87, 187, 6, 'story', 'Rebuild the full story of ''verisimilitude''—beware the five false centuries. Conquer the beast for double silk.', '{"turns":["She was born in rhetoric—the orator''s art of seeming true when proof was distant.","She became the soul of narrative—what made fiction teach by feeling real.","She arrived in England as the measure of art—not truth, but its convincing performance.","Realists demanded research and detail; she grew exacting, almost scientific.","In courtrooms and cinema, she became evidence and spectacle—belief without certainty.","She was worshipped as a goddess of wisdom in Byzantine temples—where truth and beauty merged in golden mosaics.","She traveled the Silk Road as a merchant''s guarantee of quality—the seal that promised authenticity across distant lands.","She became a painter''s technique for perfect perspective—where mathematical precision created the illusion of reality.","She was debated in salons as the measure of moral virtue—the standard by which character could be judged.","She was banned by reformers as a form of Catholic deceit—condemned for leading souls away from divine truth."],"settings":["Rome","Medieval Scholarship","English Enlightenment","Realist Movement","Modern Media","Byzantine Empire","Islamic Golden Age","Italian Renaissance","French Salons","Protestant Reformation"],"red_herrings":["She inspired medieval illuminated manuscripts depicting truth as light.","She was encoded in Renaissance geometry as divine proportion.","She emerged in Romantic poetry as nature''s authentic voice.","She became a Victorian virtue signaling moral character.","She was rejected by modernists as bourgeois pretense."],"time_periods":["1st c. CE — Rome","14th c. — Medieval Scholarship","17th c. — English Enlightenment","19th c. — Realist Movement","20th c. — Modern Media","5th c.","11th c.","15th c.","18th c.","16th c."]}'::jsonb, '{"1st c. CE — Rome → She was born in rhetoric—the orator''s art of seeming true when proof was distant.","14th c. — Medieval Scholarship → She became the soul of narrative—what made fiction teach by feeling real.","17th c. — English Enlightenment → She arrived in England as the measure of art—not truth, but its convincing performance.","19th c. — Realist Movement → Realists demanded research and detail; she grew exacting, almost scientific.","20th c. — Modern Media → In courtrooms and cinema, she became evidence and spectacle—belief without certainty.","21st c. — Digital Age → Now she moves through deepfakes and virtual worlds, engineering the feeling of truth."}', '{"feedback":{"fail":"The beast of confusion claimed you. Truth''s likeness remains hidden.","hint":"Illusions multiply—seek the thread from rhetoric to simulation.","success":"You have pierced the veil of false centuries. Double silk earned."},"hard_mode_penalty":{"health_loss_on_fail":2,"reward_multiplier_on_success":2},"allow_partial_credit":false,"shuffle_each_attempt":true}'::jsonb, 50, '"2025-10-23T00:07:43.902Z"'::jsonb, '"2025-10-23T00:07:43.902Z"'::jsonb) ON CONFLICT (word_id, level) DO UPDATE SET updated_at = EXCLUDED.updated_at WHERE word_id = EXCLUDED.word_id AND level = EXCLUDED.level;
