INSERT INTO story_comprehension_questions (id, word_id, century, question, options, correct_answer, explanation, created_at, updated_at) VALUES (48, 42, '14', 'How did medieval warfare transform ''scattershot''?', '["It made it more precise","It made it about individual accuracy","It made it about volley tactics and coverage rather than precision","It made it purely defensive"]'::jsonb, 'It made it about volley tactics and coverage rather than precision', 'The text states that ''scattershot tactics'' meant ''arrows loosed in volleys—not aimed at one target, but spread across many'' and ''Scattering became covering; precision became coverage.''', '"2025-10-24T23:01:43.433Z"'::jsonb, '"2025-10-24T23:01:43.433Z"'::jsonb) ON CONFLICT (word_id, century) DO UPDATE SET updated_at = EXCLUDED.updated_at WHERE word_id = EXCLUDED.word_id AND century = EXCLUDED.century;
INSERT INTO story_comprehension_questions (id, word_id, century, question, options, correct_answer, explanation, created_at, updated_at) VALUES (49, 42, '16', 'What changed about ''scattershot'' during the Renaissance?', '["It became more precise","It became about archery only","It became about artillery bombardment rather than archery volleys","It became purely defensive"]'::jsonb, 'It became about artillery bombardment rather than archery volleys', 'The text states that ''scattershot'' became ''what happened when you loaded a cannon with stones instead of a single ball'' and ''Scattering became artillery; coverage became bombardment.''', '"2025-10-24T23:01:43.434Z"'::jsonb, '"2025-10-24T23:01:43.434Z"'::jsonb) ON CONFLICT (word_id, century) DO UPDATE SET updated_at = EXCLUDED.updated_at WHERE word_id = EXCLUDED.word_id AND century = EXCLUDED.century;
