INSERT INTO story_comprehension_questions (id, word_id, century, question, options, correct_answer, explanation, created_at, updated_at) VALUES (63, 187, '1', 'What was the original Latin meaning of ''verisimilis''?', '["The truth itself","Like the truth or resembling truth","Completely false","Random appearance"]'::jsonb, 'Like the truth or resembling truth', 'The text explicitly states that ''verisimilis'' meant ''like the truth'' and emphasizes it was ''the appearance of truth, the semblance of reality'' with examples of actors and orators creating resemblance.', '"2025-10-24T23:01:43.443Z"'::jsonb, '"2025-10-24T23:01:43.443Z"'::jsonb) ON CONFLICT (word_id, century) DO UPDATE SET updated_at = EXCLUDED.updated_at WHERE word_id = EXCLUDED.word_id AND century = EXCLUDED.century;
