INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (42, 'scattershot', 'adjective', 'lacking focus or organization; covering many things in a random or haphazard way.', 'His scattershot approach to problem-solving wasted time and resources.', ARRAY["haphazard","indiscriminate","unfocused","broad-brush","random"], ARRAY["targeted","systematic","methodical","precise","focused"], '{}'::jsonb, 'désordonné — disorganized; ''à la volée'' captures the sense of randomness.', 'беспорядочный (besporyadochnyy) — chaotic, lacking order.', 'C1', NULL, false, '2025-10-21T02:04:31.782Z', NULL, NULL, '‘Scattershot’ marks the moralization of precision. What began as a hunter’s pattern of spread became a culture’s anxiety about control and coherence.', ARRAY["scattershot approach — unfocused or uncoordinated method (management, policy)","scattershot strategy — diffuse plan lacking prioritization (business, politics)","scattershot criticism — broad, indiscriminate attack (journalism, debate)"], '{}'::jsonb, '{"primary":"Covering a wide area or range without clear focus or direction.","tertiary":"Figurative: attempting too much or acting without strategic aim.","secondary":"Random or haphazard; lacking method or precision."}'::jsonb, ARRAY[], ARRAY["randomness","diffusion","lack of focus","breadth","chaos"], ARRAY[], ARRAY[], ARRAY["désordonné","épars","aléatoire"], ARRAY["disperser","tir","éparpiller"], ARRAY["разбросанный","хаотичный","беспорядочный"], ARRAY["разброс — scatter"], ARRAY["scattershot approach — effort without aim.","scattershot policy — governance without focus.","scattershot criticism — wide but shallow attack."], 'Scattershot lives in the moment before aim. He bursts outward—bright, loud, everywhere at once. He’s energy without a center, ambition before direction. Some call him reckless; others, free. He doesn’t choose a single path—he takes them all, and lets the world decide what lands.', 'learned') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
