INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (83, 'omit', 'verb', 'to leave out or exclude intentionally or accidentally.', 'He omitted the crucial detail from his report.', ARRAY["exclude","leave out","skip","neglect","ignore"], ARRAY["include","retain","insert","add"], '{}'::jsonb, 'omettre — neutral register; also means ''to skip'' or ''neglect''.', 'опустить (opustit’) — to omit or leave out; also ''to lower''.', 'B2', NULL, false, '2025-10-21T02:04:31.856Z', NULL, NULL, 'Omit tells the story of absence acquiring meaning. He begins as restraint, becomes sin, turns bureaucratic, and ends editorial—proof that even nothingness, named often enough, becomes an act.', ARRAY["sin of omission — failure to act when morally required (theological phrase)","lie of omission — misleading silence (moral idiom)","errors and omissions — legal formula (contracts, insurance)","omit from record — exclusion from documentation (bureaucratic)","by omission — framing absence as shaping meaning (rhetorical use)"], '{}'::jsonb, '{"primary":"To leave out or fail to include.","tertiary":"To exclude intentionally or accidentally from a record, statement, or act.","secondary":"To neglect to do or say something that should be done."}'::jsonb, ARRAY["omitted","omitting","omission"], ARRAY["absence","exclusion","neglect","editing"], ARRAY[], ARRAY[], ARRAY["omettre","négliger"], ARRAY["commettre","soumettre","permettre"], ARRAY["опускать","пропускать","упускать"], ARRAY[], ARRAY["sin of omission — moral failure by inaction.","lie of omission — deception by silence.","errors and omissions — legal safeguard.","omit from record — bureaucratic exclusion.","by omission — rhetorical presence of absence."], 'Omit is quiet company. He doesn’t strike; he slips away. He’s the space left where something should have been—the pause, the blank, the line forgotten to be drawn. His power is absence itself: what’s unsent, unsaid, undone. He never lies, yet nothing hides better than he does.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
