INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (187, 'verisimilitude', 'noun', 'the appearance or quality of being true or real; the quality of seeming to be true.', 'The novel''s verisimilitude made readers believe the events had actually occurred.', ARRAY["plausibility","believability","realism","authenticity","credibility"], ARRAY["implausibility","incredibility","unreality","falseness"], '{}'::jsonb, 'vraisemblance — the appearance of truth; used in literature and philosophy.', 'правдоподобие (pravdopodobie) — truthlikeness, plausibility.', 'C2', NULL, false, '2025-10-21T18:12:26.362Z', NULL, NULL, 'Verisimilitude traces the journey from philosophical category to aesthetic principle to technological standard. Born in rhetoric''s gray zone between truth and persuasion, she became the measure by which we judge what feels real—proof that likeness can be as powerful as truth itself.', ARRAY["verisimilitude in fiction — believability in storytelling (literary criticism)","lack verisimilitude — fail to seem realistic or plausible (critique)","lend verisimilitude — add realistic detail to enhance believability (craft)"], '{}'::jsonb, '{"primary":"The appearance or semblance of truth; the quality of appearing to be true or real.","tertiary":"In philosophy and law: the quality of being probable or likely based on available evidence.","secondary":"In literature and art: lifelike quality that makes fiction believable."}'::jsonb, ARRAY["verisimilar"], ARRAY["truth","appearance","believability","realism","probability"], 