INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (443, 'attest', 'verb', 'to provide evidence or proof of something; to bear witness.', 'The ancient ruins attest to the glory of a long-vanished civilization.', ARRAY["testify","verify","confirm","certify","validate","affirm"], ARRAY["deny","refute","dispute","contradict"], '{}'::jsonb, 'attester — formal verification, legal or official confirmation.', 'свидетельствовать (svidetel''stvovat'') — to testify or give evidence.', 'C1', NULL, false, '2025-10-25T22:31:27.078Z', NULL, NULL, 'Attest shows how truth moved from the witness''s body to the document''s seal. It''s the story of presence becoming proof, of the human voice becoming institutional record.', ARRAY["attest to — provide evidence of something (formal writing, academic)","attest that — formally declare or certify (legal, official documents)","attest by — verify through specific means or witnesses (legal testimony)"], '{}'::jsonb, '{"primary":"To provide clear evidence or proof of something''s existence or truth.","tertiary":"To serve as a witness or testimony to a fact or event.","secondary":"To declare or certify as true, especially in a formal or official context."}'::jsonb, ARRAY["attestation","attested","attesting"], ARRAY["evidence","proof","verification","witness","confirmation"], ARRAY[], ARRAY[], ARRAY["témoigner","certifier","confirmer"], ARRAY["témoin — witness","testament"], ARRAY["свидетельствовать","подтверждать","удостоверять"], ARRAY["свидетель — witness","засвидетельствовать — to certify"], ARRAY["attest to — provide evidence of something's truth or existence.","attest that — formally declare or certify a statement.","attested by — verified or confirmed through specific means or witnesses."], 'Attest holds up the mirror to truth. He''s the standing stone that says yes—this happened, this is real, I was there. He speaks with the weight of presence. You find him in courts and archives, in sworn statements and ancient scrolls. He doesn''t argue; he simply stands before what he has witnessed and will not look away.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
