INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (469, 'lumbering', 'adjective', 'moving in a slow, heavy, awkward way.', 'The lumbering bear crashed through the undergrowth.', ARRAY["clumsy","awkward","ungainly","clodding","plodding"], ARRAY["graceful","agile","nimble","elegant","swift"], '{}'::jsonb, 'lourd — heavy in movement; ''se déplacer lourdement'' for the action.', 'неуклюжий (neuklyuzhiy) — clumsy or awkward in movement.', 'B2', NULL, false, '2025-10-25T22:31:27.499Z', NULL, NULL, 'Lumbering moved from stored goods to falling timber to moving creatures to industrial machines. It''s the story of weight finding motion, of bulk becoming gait.', ARRAY["lumbering bear — large animal moving heavily (natural description)","lumbering giant — oversized thing or person moving awkwardly (figurative)","lumbering through — moving heavily through space or effort (narrative writing)"], '{}'::jsonb, '{"primary":"Moving in a heavy, ungraceful manner, often with a sense of size or weight.","tertiary":"Metaphorically, suggesting a process or system that moves slowly and inefficiencies.","secondary":"Characterized by slow, awkward, or ponderous movement."}'::jsonb, ARRAY["lumber","lumbered"], ARRAY["movement","weight","awkwardness","slowness","clumsiness"], ARRAY[], ARRAY[], ARRAY["lourd","maladroit","pesant"], ARRAY["lombard — Lombard, relating to northern Italian merchants"], ARRAY["неуклюжий","тяжёлый","неловкий"], ARRAY[], ARRAY["lumbering bear — large animal moving heavily through terrain.","lumbering giant — oversized thing moving awkwardly.","lumbering through — moving heavily and deliberately through space."], 'Lumbering is what weight looks like when it moves. He''s the sound of too much mass in motion—earth shaking, trees falling, things too big for their own feet. He''s not evil, just big. You feel him in the thunder of hooves, in the crash of freight. He makes no apology for his size. The world yields before his slow, certain advance.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
