INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (483, 'scurry', 'verb, noun', '(verb) to move hurriedly with short, quick steps. (noun) a hurried movement or activity.', 'The mice scurry into their holes when they hear a sound.', ARRAY["hurry","rush","dart","dash","scuttle"], ARRAY["amble","saunter","stroll","linger"], '{}'::jsonb, 'se précipiter — to rush or hurry, often with quick, short steps.', 'суетиться (suetit''sya) — to bustle or hurry about.', 'B2', NULL, false, '2025-10-25T22:31:27.544Z', NULL, NULL, 'Scurry shows how small, urgent movement became the signature gait of everything hunted, busy, or distracted. It''s the story of haste made small, of hurry reduced to furtive dash.', ARRAY["scurry about — move hurriedly from place to place (descriptive writing)","scurry away — hurry off quickly, often in fear (narrative)","scurry of activity — a flurry of hurried actions (metaphorical noun)"], '{}'::jsonb, '{"primary":"To move quickly with short, rapid steps, often suggesting nervousness or urgency.","tertiary":"(noun) A hurried movement or flurry of activity.","secondary":"To hurry about busily in a somewhat frantic or disorganized manner."}'::jsonb, ARRAY["scurried","scurrying"], ARRAY["movement","haste","urgency","busyness","smallness"], ARRAY[], ARRAY[], ARRAY["se précipiter","se dépêcher","courir"], ARRAY[], ARRAY["суетиться","торопиться","спешить"], ARRAY["суета — bustle, fuss"], ARRAY["scurry about — move hurriedly from place to place.","scurry away — hurry off quickly, often in fear or haste.","scurry of activity — a flurry of hurried, busy actions."], 'Scurry is motion made small and urgent. He''s the dash of tiny feet, the quick panic before flight. He doesn''t walk; he scampers. You see him in corners and cracks, in the sudden movement that makes you start. He''s not cruel, just quick. His world is hurry—the small hurry of things that must not be caught.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
