INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (496, 'steadfast', 'adjective', 'resolutely unwavering in purpose, loyalty, or faith; firmly fixed in place.', 'She remained steadfast in her commitment to justice.', ARRAY["loyal","faithful","constant","unwavering","resolute","firm"], ARRAY["fickle","unreliable","wavering","inconstant","unstable"], '{}'::jsonb, 'constant — unchanging, loyal; ''fidèle'' for faithfulness.', 'стойкий (stoykiy) — steadfast, firm; ''непоколебимый'' for unwavering.', 'C1', NULL, false, '2025-10-25T22:31:27.607Z', NULL, NULL, 'Steadfast moved from physical position to moral constancy to romantic devotion to authentic selfhood. It''s the story of standing that became staying, of place that became promise.', ARRAY["steadfast friend — loyal and reliable companion (descriptive, character)","steadfast loyalty — unwavering faithfulness to a cause or person (formal writing)","remain steadfast — persist firmly in purpose or belief (formal, moral discourse)"], '{}'::jsonb, '{"primary":"Firmly fixed in position or purpose; not likely to change or waver.","tertiary":"Characterized by unwavering determination or resolve.","secondary":"Loyal and committed, especially in the face of difficulty or opposition."}'::jsonb, ARRAY["steadfastly","steadfastness"], ARRAY["loyalty","constancy","firmness","faithfulness","resolve"], ARRAY[], ARRAY[], ARRAY["constant","fidèle","inébranlable"], ARRAY["stable","constance"], ARRAY["стойкий","непоколебимый","верный"], ARRAY["стоять — to stand"], ARRAY["steadfast friend — loyal and reliable companion who does not waver.","steadfast loyalty — unwavering faithfulness to a cause, person, or principle.","remain steadfast — persist firmly in purpose, belief, or commitment despite challenges."], 'Steadfast is the unmovable heart. He''s the oath that does not break, the belief that weathers storm. He doesn''t shout his loyalty; he simply stands. You find him in promises kept, in causes held when others fade. He''s not stubborn but true—the friend who stays when everyone else goes, the faith that endures when doubt walks in. His power is constancy. His virtue is time.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
