INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (511, 'elucidate', 'verb', 'to make clear and explain; to shed light on something that is obscure or difficult to understand.', 'The professor elucidated the complex theory in terms that everyone could understand.', ARRAY["explain","clarify","illuminate","expound","unfold","explicate"], ARRAY["obscure","confuse","muddle","bewilder","complicate"], '{}'::jsonb, 'élucider — same meaning; formal, academic register.', 'объяснить (obyasnit'') — to explain or clarify; ''разъяснить'' for detailed clarification.', 'C2', NULL, false, '2025-10-25T22:31:27.642Z', NULL, NULL, 'Elucidate traces the journey from passive brightness to active illumination. Starting as the quality of being clear, it became the act of making clear—from light received to light deliberately cast.', ARRAY["elucidate the meaning — explain the significance (academic, formal writing)","elucidate on — provide detailed clarification about (scholarly discourse)","elucidate a concept — make an idea clear through explanation (educational context)"], '{}'::jsonb, '{"primary":"To make something clear or easy to understand by explanation.","tertiary":"To unfold or reveal meaning through systematic explanation.","secondary":"To provide clarity and illumination on a complex or obscure subject."}'::jsonb, ARRAY["elucidated","elucidating","elucidation","elucidative"], ARRAY["explanation","clarity","light","understanding","revelation"], ARRAY[], ARRAY[], ARRAY["éclaircir","clarifier","expliquer"], ARRAY["lucide","luminosité"], ARRAY["разъяснить","просветить","осветить"], ARRAY["свет — light","ясный — clear"], ARRAY["elucidate the meaning — explain significance or interpretation.","elucidate on — provide detailed clarification or expansion.","elucidate a concept — make an idea clear through systematic explanation."], 'Elucidate is the breaking of dawn over confused minds. He arrives quietly, bringing light where shadows gathered. He doesn''t force understanding; he removes the veil. You find him in patient teachers, in clear arguments, in moments when the complex suddenly yields its secret. His gift is illumination—not the sudden flash but the steady glow that reveals what was always there, waiting to be seen.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
