INSERT INTO vocab_entries (id, word, part_of_speech, modern_definition, usage_example, synonyms, antonyms, collocations, french_equivalent, russian_equivalent, cefr_level, pronunciation, is_mastered, date_added, story_text, contrastive_opening, structural_analysis, common_collocations, metadata, definitions, variant_forms, semantic_field, english_synonyms, english_antonyms, french_synonyms, french_root_cognates, russian_synonyms, russian_root_cognates, common_phrases, story_intro, learning_status) VALUES (527, 'plausible', 'adjective', 'seeming reasonable or probable; appearing worthy of belief or acceptance.', 'Her explanation sounded plausible, but we needed more evidence.', ARRAY["credible","believable","reasonable","convincing","persuasive","specious"], ARRAY["implausible","incredible","unbelievable","absurd","ridiculous"], '{}'::jsonb, 'plausible — same meaning; ''vraisemblable'' as alternative.', 'правдоподобный (pravdopodobnyy) — seeming true or believable.', 'B2', NULL, false, '2025-10-25T22:31:27.715Z', NULL, NULL, 'Plausible moved from theatrical applause to philosophical probability to social respectability to persuasive strategy. Her journey tracks the shift from ''worthy of approval'' to ''appearing reasonable''—from judgment to appearance.', ARRAY["plausible explanation — believable account or reason (general use)","plausible deniability — ability to deny involvement convincingly (legal, political)","sound plausible — appear reasonable upon hearing (conversational)"], '{}'::jsonb, '{"primary":"Appearing reasonable or credible; seeming worthy of belief.","tertiary":"Capable of being applauded or approved as reasonable.","secondary":"Having the appearance of truth without necessarily being true."}'::jsonb, ARRAY["plausibility","plausibly"], ARRAY["believability","credibility","reasonableness","appearance","persuasion"], ARRAY[], ARRAY[], ARRAY["vraisemblable","crédible","raisonnable"], ARRAY["applaudir","applaudissement"], ARRAY["правдоподобный","убедительный","разумный"], ARRAY["аплодировать — to applaud"], ARRAY["plausible explanation — believable account that seems reasonable.","plausible deniability — ability to deny involvement convincingly.","sound plausible — appear reasonable when heard or considered."], 'Plausible sits at the threshold of belief. She presents herself as reasonable, dressed in the clothes of acceptability. She doesn''t promise truth—only the appearance of it. You meet her in convincing arguments that might be wrong, in stories that could be true, in explanations that feel right even when they''re incomplete. Her power is persuasion through seeming, not certainty.', 'unmastered') ON CONFLICT (id) DO UPDATE SET word = EXCLUDED.word, part_of_speech = EXCLUDED.part_of_speech, modern_definition = EXCLUDED.modern_definition, usage_example = EXCLUDED.usage_example, synonyms = EXCLUDED.synonyms, antonyms = EXCLUDED.antonyms, collocations = EXCLUDED.collocations, french_equivalent = EXCLUDED.french_equivalent, russian_equivalent = EXCLUDED.russian_equivalent, cefr_level = EXCLUDED.cefr_level, pronunciation = EXCLUDED.pronunciation, is_mastered = EXCLUDED.is_mastered, date_added = EXCLUDED.date_added, story_text = EXCLUDED.story_text, contrastive_opening = EXCLUDED.contrastive_opening, structural_analysis = EXCLUDED.structural_analysis, common_collocations = EXCLUDED.common_collocations, metadata = EXCLUDED.metadata, definitions = EXCLUDED.definitions, variant_forms = EXCLUDED.variant_forms, semantic_field = EXCLUDED.semantic_field, english_synonyms = EXCLUDED.english_synonyms, english_antonyms = EXCLUDED.english_antonyms, french_synonyms = EXCLUDED.french_synonyms, french_root_cognates = EXCLUDED.french_root_cognates, russian_synonyms = EXCLUDED.russian_synonyms, russian_root_cognates = EXCLUDED.russian_root_cognates, common_phrases = EXCLUDED.common_phrases, story_intro = EXCLUDED.story_intro, learning_status = EXCLUDED.learning_status;
